  function dragged(d) {
    //var pos = d3.mouse(svg.node());
    d.fx = d3.event.x;
    d.fy = d3.event.y;
  }

  function dragended(d) {
    //if (!event.active) simulation.alphaTarget(0);
    simulation.alphaTarget(0);
    d.fx = null;
    d.fy = null;
  }

  return d3.drag()
      .on("start", dragstarted)
      .on("drag", dragged)
      .on("end", dragended);
}

function chartFun() {
  //var width=600, height=600;
  //var svg = d3.select('body').append('svg').attr('width', width).attr('height', height);
  var color = d3.scaleOrdinal(d3.schemeSet2);

  const simulation = d3.forceSimulation()
      .force("charge", d3.forceManyBody()
             .strength(-100))
      .force("link", d3.forceLink()
             .id(d => d.id))
      .force("x", d3.forceX())
      .force("y", d3.forceY())
      .force("center", d3.forceCenter(width / 2, height / 2))
      .on("tick", ticked);

  let link = svg.append("g")
      .attr("stroke", "#000")
      .attr("stroke-width", 1.5)
      .attr("stroke-opacity", .3)
    .selectAll("line");

  let node = svg.append("g")
      .attr("stroke", "#fff")
      .attr("stroke-width", 1.5)
      .attr("cursor", "crosshair")
    .selectAll("circle");

  function ticked() {

    node.attr("cx", d => d.x)
        .attr("cy", d => d.y)

    link.attr("x1", d => d.source.x)
        .attr("y1", d => d.source.y)
        .attr("x2", d => d.target.x)
        .attr("y2", d => d.target.y);
  }

  return Object.assign( svg.node(), {
    update({nodes, links, covariate}) {
      const old = new Map(node.data().map(d => [d.id, d]));
      nodes = nodes.map(d => Object.assign(old.get(d.id) || {}, d));
      links = links.map(d => Object.assign({}, d));

      node = node
        .data(nodes, d => d.id)
        .join(enter => enter.append("circle")
          .attr("r", 8)
          .attr("fill", d => color(d[covariate])))
        .call(node => node.append("title").text(d => covariate + ": " + d[covariate]))
        .call(drag(simulation));

      link = link
        .data(links, d => [d.source, d.target])
        .join("line");

      simulation.nodes(nodes);
      simulation.force("link").links(links);
      simulation.alpha(1).restart();
  }});
}

function update() {
  const nodes = dataNodes;
  const links = dataEdges;
  const covariate = "name"; //"Age","Gender","Office","Practice","School","vertex.names","Years" 

  chartObj = chartFun();
  chartObj.update({nodes, links, covariate});
}


update();

